  const [message, setMessage] = useContext(MessageContext)


  return (
    <div className={`text-fit ${styles.grandparent}`}>
      <h1>Grandparent</h1>
      <div>
        This state message was <span className="highlight">{message}</span>.
      </div>

      <Parent />

    </div>
  );
}
export default Grandparent;
